  <img id="slide2" src="images/sources_of_anomalies_1.png" width="100%">
  <br>
  <button onclick="prev2()">Previous</button>
  <button onclick="next2()">Next</button>
</div>

<script>
(() => {
  const images2 = [
    "images/sources_of_anomalies_1.png",
    "images/sources_of_anomalies_2.png",
    "images/sources_of_anomalies_3.png",
    "images/sources_of_anomalies_4.png",
    "images/sources_of_anomalies_5.png",
    "images/sources_of_anomalies_6.png",
    "images/sources_of_anomalies_7.png",
    "images/sources_of_anomalies_8.png",
    "images/sources_of_anomalies_9.png",
    "images/sources_of_anomalies_10.png",
    "images/sources_of_anomalies_11.png",
    "images/sources_of_anomalies_12.png"
  ];
  let index2 = 0;

  function show2() {
    document.getElementById("slide2").src = images2[index2];
  }

  window.next2 = () => { index2 = (index2 + 1) % images2.length; show2(); };
  window.prev2 = () => { index2 = (index2 - 1 + images2.length) % images2.length; show2(); };
})();
</script>

